# contract
master_chef_contract = web3.eth.contract(address=master_chef_address, abi=master_chef_abi)

total_alloc_point = master_chef_contract.functions.totalAllocPoint().call()
pool_length = master_chef_contract.functions.poolLength().call()

sushi_usdc_pool_contract = web3.eth.contract(address=sushi_usdc_pool_address, abi = pool_abi)
reserve_info = sushi_usdc_pool_contract.functions.getReserves().call()
sushi_reserve = reserve_info[0]
usdc_reserve = reserve_info[1]
# price calculation based on SUSHI/USDC pool at sushiswap (excluded trading fee)
# sushi decimal = 18, therefore 1 sushi = 1e18
sushi_usdc_price = (usdc_reserve * 1e18 / (sushi_reserve + 1e18))/1e6
# pool id of usdc-weth
usdc_weth_pid = 1

# Get pool info
# Pool Info Returns:
# 1. address of lp token
# 2. allocation point
# 3. Last block number that SUSHIs distribution occured
# 4. Accumulated SUSHIs per share, times 1e12
pool_info = master_chef_contract.functions.poolInfo(usdc_weth_pid).call()
pool_address = pool_info[0]
pool_alloc_point = pool_info[1]

# create weth-usdc pool contract object
pool_contract = web3.eth.contract(address=pool_address, abi=pool_abi)

# token0_address = pool_contract.functions.token0().call()
# token1_address = pool_contract.functions.token1().call()

# sushi reward per block
total_sushi_per_block = master_chef_contract.functions.sushiPerBlock().call()
pool_weight = pool_alloc_point / total_alloc_point

pool_sushi_per_block = total_sushi_per_block * pool_weight / 1e18
# token0_contract = web3.eth.contract(address=token0_address, abi=ERC20_abi)
# token1_contract = web3.eth.contract(address=token1_address, abi=ERC20_abi)
# print(token0_contract.functions.symbol().call())
# print(token1_contract.functions.symbol().call())

reserve_info = pool_contract.functions.getReserves().call()

# same calculation method as sushi price above
usdc_reserve = reserve_info[0]
weth_reserve = reserve_info[1]
weth_usdc_price = usdc_reserve * 1e18 / (weth_reserve + 1e18)

# assuming constant price, unlimited liquidity (no change in liquidity pool)
tvl_usdc = (weth_reserve/1e18 * weth_usdc_price + usdc_reserve) / 1e6

# assume 6000 blocks per day
interest_rate = (pool_sushi_per_block * 6000 * 365 * sushi_usdc_price / tvl_usdc) * 100
print(interest_rate)
